sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var endTime = g_form.getValue('end_time');
    var startTime = new Date(newValue);

    if (endTime) {
        endTime = new Date(endTime);

        if (startTime > endTime) {
            g_form.showFieldMsg('start_time', 'Start Time cannot be later than End Time.', 'error');
        } else {
            g_form.hideFieldMsg('start_time'); 
        }
    }
}
